mapper.select:
SELECT
-- 级联表的字段 ,yonghu.username as username ,yonghu.password as password ,yonghu.name as name ,yonghu.phone as phone ,yonghu.id_number as idNumber ,yonghu.sex_types as sexTypes ,yonghu.age as age ,yonghu.bumen_types as bumenTypes ,yonghu.zhiwei_types as zhiweiTypes ,yonghu.my_photo as myPhoto ,yonghu.nation as nation ,yonghu.politics_types as politicsTypes ,yonghu.birthplace as birthplace
FROM xiangmu a left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.yonghu_id = #{params.yonghuId}
and a.xiangmu_name like CONCAT('%',#{params.xiangmuName},'%')
and a.xiangmu_types = #{params.xiangmuTypes}
and a.xiangmujindu_types = #{params.xiangmujinduTypes}
and a.xiangmu_content like CONCAT('%',#{params.xiangmuContent},'%')
and DATE_FORMAT(a.insert_time, '%Y-%m-%d %H:%T:%s') >= DATE_FORMAT(#{params.insertTimeStart}, '%Y-%m-%d %H:%T:%s')
and DATE_FORMAT(a.insert_time, '%Y-%m-%d %H:%T:%s') <= DATE_FORMAT(#{params.insertTimeEnd} , '%Y-%m-%d %H:%T:%s')
and DATE_FORMAT(a.update_time, '%Y-%m-%d %H:%T:%s') >= DATE_FORMAT(#{params.updateTimeStart}, '%Y-%m-%d %H:%T:%s')
and DATE_FORMAT(a.update_time, '%Y-%m-%d %H:%T:%s') <= DATE_FORMAT(#{params.updateTimeEnd} , '%Y-%m-%d %H:%T:%s')
and yonghu.username like CONCAT('%',#{params.username},'%')
and yonghu.password like CONCAT('%',#{params.password},'%')
and yonghu.name like CONCAT('%',#{params.name},'%')
and yonghu.phone like CONCAT('%',#{params.phone},'%')
and yonghu.id_number like CONCAT('%',#{params.idNumber},'%')
and yonghu.sex_types = #{params.sexTypes}
and yonghu.age >= #{params.ageStart}
and yonghu.age <= #{params.ageEnd}
and yonghu.bumen_types = #{params.bumenTypes}
and yonghu.zhiwei_types = #{params.zhiweiTypes}
and yonghu.nation like CONCAT('%',#{params.nation},'%')
and yonghu.politics_types = #{params.politicsTypes}
and yonghu.birthplace like CONCAT('%',#{params.birthplace},'%')
order by a.id desc
